import React from 'react';
import styled from 'styled-components';

const AppContainer = styled.div`
  display: flex;
  flex: 1 auto;
  height: 100vh;
  background-color: grey;
`;

const Grid = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex: 1 auto;
  border: 1px solid red;
`;

const Row = styled.div`
  display: flex;
  flex: 0 50%;
  height: calc( 100% / 9 );
  border: 1px solid black;
`;

const Cell = styled.div`
  display: flex;
  flex: 1 auto;
  height: 100%;
  border: 1px dashed white;
`;

function App() {

  const gridSize = 9;
  let rows = [];
  let cells = [];

  for(let i = 0; i < gridSize; i++) {

    for (let j = 0; j < gridSize; j++) {
      cells.push(<Cell coordinate={`${i}-${j}`} />);
    }

    rows[i] = cells;
    cells=[];

  }

  return (
    <AppContainer>
      <Grid>
        {
          rows &&
          rows.map((row, index) => {
            console.log(row);
            return(
              <Row key={index} />
            );
          })
        }
      </Grid>
    </AppContainer>
  );
}

export default App;
