import React from 'react';
import styled from 'styled-components';

const AppContainer = styled.div`
  display: flex;
  flex: 1 auto;
  height: 100vh;
  background-color: grey;
`;

const Grid = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex: 1 auto;
  border: 1px solid red;
`;

const Row = styled.div`
  display: flex;
  flex: 0 50%;
  height: calc( 100% / 9 );
  border: 1px solid black;
`;

const Cell = styled.div`
  display: flex;
  flex: 1 auto;
  height: 100%;
  border: 1px dashed white;
`;

function App() {
  const gridSize = 9;


  return (
    <AppContainer>
      <Grid>
        <Row>
          {
            gridSize &&
            gridSize.map( (index) => {
              <Cell location={index} />
            }) 
          }
        </Row>
      </Grid>
    </AppContainer>
  );
}

export default App;
