import React, { useState } from 'react';
import styled, {keyframes} from 'styled-components';

import logo from './logo.svg';
import enemyIcon from './angular-icon.svg';


const rotate = keyframes`
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
`;

const AppContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex: 1 auto;
  height: 100vh;
  background-color: #4a4a4a;
`;

const Grid = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 50vw;
  height: 50vw;
`;

const Row = styled.div`
  display: flex;
  flex: 0 0 calc(100% / 9);
  width: 100%;
`;

const Cell = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex: 0 0 calc(100% / 9);
  height: 100%;
  border: 1px dashed #ececec;
  overflow: hidden;
  cursor: pointer;

  img {
    display: flex;
    width: 80%;
    height: auto;
  }
`;

function App() {
  const [playerPosition, updatePlayerPosition] = useState({x: 0, y: 0});
  const [enemyPosition, updateEnemyPosition] = useState({x: 8, y: 8});

  const gridSize = 9;
  let rows = [];
  let cells = [];

  for(let i = 0; i < gridSize; i++) {

    for (let j = 0; j < gridSize; j++) {
      cells.push(<Cell coordinate={`${i}-${j}`} xCoordinate={i} yCoordinate={j} />);
    }

    rows[i] = cells;
    cells=[];
  }

  const playerMove = (x, y) => {
    console.log(`moving to ${x}, ${y}`);
    if(playerPosition.x !== x || playerPosition.y !== y) {
      updatePlayerPosition({x: x, y: y});
    }
  };

  return (
    <AppContainer>
      <Grid>
        {
          rows &&
          rows.map((row, index) => {
            return(
              <Row key={index}>
                {
                  row &&
                  row.map((cell, index) => {
                    return(
                      <Cell 
                        key={`${cell.coordinate}-${index}`}
                        onClick={ () => { playerMove(cell.props.xCoordinate, cell.props.yCoordinate)} }
                      >

                        {
                          playerPosition.x === cell.props.xCoordinate &&
                          playerPosition.y === cell.props.yCoordinate &&
                          <img className='player' src={logo} alt='player icon' />
                        }

                        {
                          enemyPosition.x === cell.props.xCoordinate &&
                          enemyPosition.y === cell.props.yCoordinate &&
                          <img className='enemy' src={enemyIcon} alt='enemy icon' />
                        }

                      </Cell>
                    );
                  })
                }
              </Row>
            );
          })
        }
      </Grid>
    </AppContainer>
  );
}

export default App;
