import React, { useState } from 'react';
import styled from 'styled-components';

import logo from './logo.svg';

const AppContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex: 1 auto;
  height: 100vh;
  background-color: #4a4a4a;
`;

const Grid = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 50vw;
  height: 50vw;
`;

const Row = styled.div`
  display: flex;
  flex: 1 auto;
  width: 100%;
`;

const Cell = styled.div`
  display: flex;
  flex: 1 auto;
  height: 100%;
  border: 1px dashed #ececec;
`;

function App() {
  const [playerPosition, updatePlayerPosition] = useState({x: 0, y: 0});

  const gridSize = 9;
  let rows = [];
  let cells = [];

  for(let i = 0; i < gridSize; i++) {

    for (let j = 0; j < gridSize; j++) {
      cells.push(<Cell coordinate={`${i}-${j}`} xCoordinate={i} yCoordinate={j} />);
    }

    rows[i] = cells;
    cells=[];
  }

  return (
    <AppContainer>
      <Grid>
        {
          rows &&
          rows.map((row, index) => {
            return(
              <Row key={index}>
                {
                  row &&
                  row.map((cell, index) => {
                    console.log(cell);
                    return(
                      <Cell 
                        key={`${cell.coordinate}-${index}`}
                      >
                        {
                          playerPosition.x === cell.xCoordinate &&
                          playerPosition.y === cell.yCoordinate &&
                          <div className='currentLocation'>
                            HI
                          </div>
                        } 
                      </Cell>
                    );
                  })
                }
              </Row>
            );
          })
        }
      </Grid>
    </AppContainer>
  );
}

export default App;
