import React from 'react';
import styled from 'styled-components';

const AppContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex: 1 auto;
  height: 100vh;
  background-color: #4a4a4a;
`;

const Grid = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 50vw;
  height: 50vw;
  border: 1px solid red;
`;

const Row = styled.div`
  display: flex;
  flex: 1 auto;
  width: 100%;
  border: 1px solid black;
`;

const Cell = styled.div`
  display: flex;
  flex: 1 auto;
  height: 100%;
  border: 1px dashed white;
`;

function App() {

  const gridSize = 9;
  let rows = [];
  let cells = [];

  for(let i = 0; i < gridSize; i++) {

    for (let j = 0; j < gridSize; j++) {
      cells.push(<Cell coordinate={`${i}-${j}`} />);
    }

    rows[i] = cells;
    cells=[];

  }

  return (
    <AppContainer>
      <Grid>
        {
          rows &&
          rows.map((row, index) => {
            return(
              <Row key={index}>
                {
                  row &&
                  row.map((cell, index) => {
                    console.log(cell);
                    return(
                      <Cell key={cell.coordinate} />
                    );
                  })
                }
              </Row>
            );
          })
        }
      </Grid>
    </AppContainer>
  );
}

export default App;
